import { useEffect, useState } from "react";
import axios from "axios";
import { Button, Table, Image } from "antd";
interface iItem {
  _id: string;
  title: string;
  link: string;
  price: string;
  imgUrls: string[];
}

const DISMISSED_ITEMS_KEY = "dismissedItems";
const API_URL =
  "https://63cxsi7av7c4cmunrrml5wo2qy0msxtk.lambda-url.us-east-2.on.aws/";

const getLocalDismissedItems = () =>
  JSON.parse(window.localStorage.getItem(DISMISSED_ITEMS_KEY) || "[]");

export const GearFinder = () => {
  const [data, setData] = useState([]);
  const [dismissedItems, setDismissedItems] = useState<string[]>(
    getLocalDismissedItems()
  );

  const fetchData = async () => {
    const res = await axios(API_URL);
    setData(res.data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    window.localStorage.setItem(
      DISMISSED_ITEMS_KEY,
      JSON.stringify(dismissedItems)
    );
  }, [dismissedItems]);

  const handleShowAllItems = () => setDismissedItems([]);

  const handleSetDismissedItems = (id: string) => {
    setDismissedItems((ids) => [...ids, id]);
  };

  const columns = [
    {
      dataIndex: "_id",
      ket: "_id",
      render: (id: string) => (
        <Button onClick={() => handleSetDismissedItems(id)}>Hide</Button>
      ),
      width: 70,
    },
    {
      dataIndex: "id",
      key: "id",
      render: (id: string, item: iItem) => (
        <Image
          src={item.imgUrls[0]}
          style={{ width: 100, height: 100 }}
          fallback="data:image/png;base64,"
        />
      ),
      width: 100,
    },
    {
      dataIndex: "title",
      key: "title",
      render: (title: string, item: iItem) => (
        <a href={item.link} target="_blank" rel="noreferrer">
          {title}
        </a>
      ),
    },
    {
      dataIndex: "title",
      key: "title",
      render: (title: string, item: iItem) => (
        <a
          href={`https://www.sweetwater.com/store/search.php?s=${title}`}
          target="_blank"
          rel="noreferrer"
        >
          Search Sweetwater
        </a>
      ),
    },
    {
      dataIndex: "price",
      key: "price",
    },
  ];

  const filteredData = data.filter(
    (i: iItem) => !dismissedItems.includes(i._id)
  );

  return (
    <>
      <Table
        loading={data.length === 0}
        title={() => (
          <Button onClick={handleShowAllItems}>
            Show {dismissedItems.length} Hidden Items
          </Button>
        )}
        columns={columns}
        dataSource={filteredData}
      />
    </>
  );
};
